INSERT INTO `goods_goods` VALUES (20, '', 'A股历史行情', 479, 9224, 8924, 999, 0, 0, '回顾历史，方能展望未来', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<span style=\"font-size:16px;\"><strong><span style=\"font-size:16px;\"> </span></strong></span>\n</p>\n<p>\n<strong>一句话介绍：</strong><span style=\"font-size:16px;\">定位于提供上市公司股票行情、资金流向、融资融券数据信息。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong><span style=\"font-size:16px;\">服务介绍：</span></strong><span style=\"font-size:16px;\">该模块收录上市公司股票行情、资金流向、融资融券等内容，其中股票行情按日、月、年、区间不同口径分别统计，资金流向分为每日资金流向和区间资金流向，融资融券分为每日融资融券和区间融资融券。</span></span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong><span style=\"font-size:16px;\">服务优势：</span></strong><span style=\"font-size:16px;\">行情数据来自交易所每日收盘文件及交易所官方网站披露的交易统计数据，官方的数据源确保了数据的准确性与可靠性；提供大量行情衍生类指标；提供复权行情</span></span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong><span style=\"font-size:16px;\">应用场景：</span></strong><span style=\"font-size:16px;\">APP、金融终端、行情分析类文章、网站、量化研究等</span></span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><strong><span style=\"font-size:16px;\">咨询电话：</span></strong><span style=\"font-size:16px;\">0571-26693847</span></span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\"></span><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><br/>\n</span></span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"> </span></span>\n</p>\n<p>\n<strong><span style=\"font-size:16px;\">常见问题：</span></strong>\n</p>\n<p>\n<strong><span style=\"font-size:16px;\"><img alt=\"\" src=\"https://www.hscloud.cn/api/201711291710400009.png\"/><br/>\n</span></strong>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<br/>\n</p></div>', 1, 'goods/images/20.jpg', 0, 0, '2018-12-22 10:59:43.363668', 2);
