INSERT INTO `goods_goods` VALUES (55, '', 'iWin智能运营', 612, 5088, 7077, 999, 0.1, 0.1, '精细化智能运营。', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<span style=\"font-size:16px;line-height:1.5;\">iWin是一个面向目标用户通过合适内容，在合适时间、合适触点与用户进行交互的精细化智能运营平台，旨在帮助企业提高运营效率，提升企业ROI。</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\"><strong>平台架构</strong></span>\n</p>\n<p>\n<img align=\"\" alt=\"\" height=\"395\" src=\"https://www.hscloud.cn/api/201805141539420020.png\" title=\"\" width=\"800\"/><span style=\"font-size:16px;line-height:1.5;\"></span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\"><strong><br/>\n</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\"><strong>平台主要功能：</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\">1、自动化运营</span>\n</p>\n<p>\n<img align=\"\" alt=\"\" height=\"383\" src=\"https://www.hscloud.cn/api/201805141540200021.png\" title=\"\" width=\"800\"/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<img align=\"\" alt=\"\" height=\"379\" src=\"https://www.hscloud.cn/api/201805141540310022.png\" title=\"\" width=\"800\"/>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\">2、全员营销</span>\n</p>\n<p>\n<img align=\"\" alt=\"\" height=\"364\" src=\"https://www.hscloud.cn/api/201805141540550023.png\" title=\"\" width=\"800\"/>\n</p>\n<p>\n<img align=\"\" alt=\"\" height=\"301\" src=\"https://www.hscloud.cn/api/201805141541250024.png\" title=\"\" width=\"800\"/>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\"><strong>产品优势</strong></span>\n</p>\n<p>\n<img align=\"\" alt=\"\" height=\"478\" src=\"https://www.hscloud.cn/api/201805141541540025.png\" title=\"\" width=\"800\"/>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\"><strong>应用场景</strong></span>\n</p