INSERT INTO `goods_goods` VALUES (66, '', '股价预警', 348, 7272, 6237, 999, 0.1, 0.1, '第一时间了解股价变动', '<div class=\"hide service_info\" id=\"noapi_\"><h3>\n<span style=\"font-size:16px;\">产品介绍</span>\n</h3>\n<p>\n<span style=\"font-size:16px;\">股票实时股价、涨跌幅等条件的设置、监控及提醒。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<h3>\n<span style=\"font-size:16px;\">产品功能</span>\n</h3>\n<p>\n<span style=\"font-size:16px;\">1、前端页面设置股票涨跌、涨跌幅等参数，以及展示提醒记录；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">2、实时股价、涨跌幅监控；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">3、股价和涨跌幅到达设定值触发后推送消息提醒；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<h3>\n<span style=\"font-size:16px;\">产品优势</span>\n</h3>\n<p>\n<span style=\"font-size:16px;\">高效、多样性地接收股票变动信息</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">应用场景</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">投资者对添加的自选股或持仓股设置提醒条件，到达触发条件自动提醒</span>\n</p>\n<p>\n<img alt=\"\" src=\"https://www.hscloud.cn/home/openapi/sharedirs_new/openportal/upload/image/ueditor/20171027/20171027164337000059.jpg\" style=\"line-height:1.5;\"/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<strong><span style=\"font-size:16px;\">如何体验</span></strong>\n</p>\n<p>\n<span style=\"font-size:16px;\">关注微信公众号</span><strong><span style=\"font-size:16px;\">云赢网络</span></strong><span style=\"font-size:16px;\">，点击服务下的股价提醒，设置需要提醒的股票价格即可；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<h3>\n<span style=\"line-height:1.5;font-size:16px;\">商务咨询</span>\n</h3>\n<p>\n<span style=\"font-size:16px;\">咨询电话：0571-26693847</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">邮箱地址：hscloud@hundsun.com</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">（本页面产品仅提供组件，不包含数据服务）</span>\n</p>\n<p>\n<br/>\n</p></div>', 1, 'goods/images/66.jpg', 0, 0, '2018-12-22 10:59:43.363668', 4);
INSERT INTO `goods_goods` VALUES (67, '', '问策组件(H5)', 216, 5870, 9421, 999, 0.1, 0.1, '想要投资策略？这里有！', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<span style=\"font-size:16px;\"><strong>服务简介：</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">问策是一个量化策略接入平台，利用云计算手段连接策略的使用者和策略的提供者。使用者可以很容易的将智能策略服务接入（通过互联网服务或者组件）到自己的系统、应用或者APP中，策略的提供方也可以很快速地发布研发的策略。对于普通投资者来说，问策平台可以根据用户的特征，提供相应的策略。问策平台会对接入的策略进行评估分类，根据策略的指标，比如收益率，波动性，alpha, beta值等等进行排序，风险等级计算等等，从而对接用户的投资偏好。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><img alt=\"\" src=\"https://www.hscloud.cn/home/openapi/sharedirs_new/openportal/upload/image/ueditor/20170827/20170827182703000017.jpg\"/><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>产品功能：</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">一、问策平台支持接入多类型的策略</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">（1）支持机器人生成的策略；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">（2）支持人工编制的策略；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">（3）支持牛人组合，高手自建组合；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">二、问策平台对策略进行分类评估</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">问策平台根据智能投顾系统提供的用户标签，向用户个性化提供不同的策略。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">三、问策平台支持多种消息推送方式</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><span style=\"font-size:16px;\">问策平台将策略产生的操盘动作及时推送给用户。在和不同客户系统对接的时候可以适配不同的推送方式。</span></span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><span style=\"font-size:16px;\">四、问策平台提供多因子选股服务</span></span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><span style=\"font-size:16px;\">用户可以对选股因子进行自定义组合。同时，问策的多因子选股服务将对选股结果进行深度回测。</span></span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><strong>咨询电话：0571-26693847</strong></span><br/>\n</span>\n</p>\n<p>\n<br/>\n</p></div>', 1, 'goods/images/67.jpg', 0, 0, '2018-12-22 10:59:43.363668', 4);
INSERT INTO `goods_goods` VALUES (68, '', 'PBOX投资终端量化版', 23, 6509, 9069, 999, 0.1, 0.1, '一站式量化策略服务平台', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<span style=\"font-size:16px;\"><strong>服务介绍</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">PBOX投资终端量化版是一款集实时行情、策略研究回测、仿真交易和实盘交易于一体的量化投研和交易平台，为个人及机构投资者提供一站式量化策略服务。</span>\n</p>\n<p>\n<span><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>服务功能</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">量化投资研究，策略回测完善，AI策略，金语言支持，仿真交易。</span>\n</p>\n<p>\n<span><br/>\n</span>\n</p>\n<p>\n<span><span style=\"font-size:16px;\"><strong>服务优势</strong></span><br/>\n</span>\n</p>\n<p>\n<span> </span>\n</p>\n<p>\n<span style=\"font-size:16px;\">1、全面、及时、准确的行情数据和财务资讯数据</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">2005年至今完整的行情数据，停复牌、复权等信息，实时同步更新；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">技术指标、财务数据、上市公司基本资料、资本运作数据、融资分红数据、宏观行业数据等。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">2、强大灵活的研究平台 </span>\n</p>\n<p>\n<span style=\"font-size:16px;\">集交互式编程和写作于一体的研究平台，说明文字、代码、图表、公式、结论都整合在一个文档中。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">3、精准回测</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">精准回测引擎：提供简洁强大的API；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">多级回测：日级、分钟级回测；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">快速验证：回测结果实时显示、快速响应、数据全面。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">4、量化投研和交易一体化解决方案</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">策略验证研究-&gt;策略回测完善-&gt;策略仿真交易-&gt;策略实盘交易；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">策略快速迭代完善，完成投研即可投入实盘交易。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">5、策略安全保障 </span>\n</p>\n<p>\n<span style=\"font-size:16px;\">独立沙箱：基于Docker容器的用户隔离技术，每个用户独享策略存储和交易运行环境；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">策略加密：利用AES/DES加密技术、混淆编译技术确保策略存储和策略传输安全。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">6、公式引擎</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">全面支持指标公式引擎，技术派福音，人人都能量化交易。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">7、量化AI引擎</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">研究模块集成基于Tensorflow的量化AI引擎；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">支持通过向导自动生成AI策略；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">支持AI策略模型回测和实盘交易。</span>\n</p>\n<p>\n<span><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>应用场景</strong></span>\n</p>\n<p>\n<span> </span>\n</p>\n<p>\n<span style=\"font-size:16px;\">1、适用于私募公司量化研究员研究报告的编写，研究模块集文本编辑、策略编写、策略回测生成图表于一体；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">2、适用于私募公司策略编写回测，支持日级、分钟级回测；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">3、提供仿真交易通道，私募公司可随时随地查看策略执行效果；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">4、提供实盘交易通道，策略回测通过后，无需另外修改可直接实盘交易，降低私募研究人员工作量；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">5、提供归因分析功能，协助私募公司评测策略效果；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">6、独有的AI策略功能，为私募研究员开发基于遗传算法的AI策略提供支持；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">7、拥有2005年以来完整的历史数据，包含：历史高频数据等；提供实时行情数据；提供上市公司技术指标、财务数据、上市公司基本资料等等。为私募公司研究人员提供完善的数据支撑。</span>\n</p>\n<p>\n<br/>\n</p>\n<span style=\"font-size:16px;\"><strong>咨询电话</strong></span>\n<p>\n<span><span style=\"font-size:16px;\">0571-26693847</span></span>\n</p></div>', 1, 'goods/images/68.jpg', 0, 0, '2018-12-22 10:59:43.363668', 4);
INSERT INTO `goods_goods` VALUES (69, '', 'iSee智能问答', 376, 7551, 8360, 999, 0.1, 0.1, '24小时贴身问答炒股聊天机器人', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<span style=\"font-size:16px;line-height:1.5;\"><strong>服务介绍</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\">24小时炒股聊天机器人贴身问答，提供今日大盘、热门板块、我的自选、建议优股、指标选股、走势形态、个股诊断、个股详情、自选分析、股市知识等十大功能板块。</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\"></span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\"><strong>服务功能</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\">1、每日建议优股：大数据算法帮您找出上涨概率最高的股票。iSee智能问答特设“建议优股”功能，从用户本身的自选股列表出发，每天基于基本面、技术面、消息面、资金面，向用户推送、展示同类股票中更优质、健康的标的；同时，用户还可通过“历史涨幅概率”与“昨日涨幅概率”了解到每项选股指标的有效记录，选出最靠谱的选股指标组合，提升自身选股、择时的能力。</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\">2、一站式选股、诊股，好股票一个都不能少：iSee智能问答提供“指标选股”“板块选股”“形态选股”三大选股方式，帮助不熟悉任何选股模型，从未学习、建立过任何交易逻辑的小白股民形成对选股能力的初步培养，同时搭配“建议优股”功能，帮助用户从无到有快速学习选股知识、建立交易逻辑、提升交易能力。</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\">3、最酷的财经百事通，最懂你的理财机器人：海量财经智能问答词库，通过机器+人工的方式完成日常内容维护，保证用户在这里可以一次性获取到最全面、最专业、最准确、最精辟、最前沿的财经知识，无需在繁复的网络信息中自行识别过时的垃圾信息和广告，帮助用户大大缩小学习成本、提升学习效率。</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\">4、今日大盘：运用实时数据精准预测股市天气，优选有效性较高的宏观指标，辅以两市涨跌数据、资金流向数据等，帮助投资者准确把握市场情绪，规避投资风险，避免高位接盘。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\"><strong>服务优势</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\">1、独家大盘风向预测，帮助投资者准确把握市场情绪，规避投资风险；</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\">2、短中长线个股报告，精准触达不同投资风格投资者的需求；</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\">3、海量大数据支持的择优替换模型，每天向用户展示同类股票中更优质、更健康的标的；</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\">4、支持看大盘、测自选、诊个股、找机会的全方位一站式机器人。</span>\n</p>\n<p>\n<br/>\n</p>\n<span style=\"font-size:16px;line-height:1.5;\"><str