INSERT INTO `goods_goods` VALUES (70, '', 'iSee账户诊断', 714, 6844, 9325, 999, 0.1, 0.1, '诊断用户交易情况，提供个性化服务', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<span style=\"font-size:16px;\"><strong>服务介绍</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">基于iSee客户画像系统形成账户诊断可为券商提供准确的业务数据及服务。帮助用户梳理整体交易状况，回溯交易细节。诊断用户交易情况，提供更个性化服务。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>服务功能</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">1、账户种类：资金帐户（含理财账户）、信用账户</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">2、</span><span style=\"font-size:16px;line-height:1.5;\">诊断维度：资产分析、交易分析、盈亏分析、综合分析</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><span style=\"font-size:16px;\">3、</span><span style=\"font-size:16px;\">时间维度</span></span><span style=\"font-size:16px;\">：</span><span style=\"font-size:16px;\">1月、3月、半年、1年</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">4、</span><span style=\"font-size:16px;\">盈亏分析</span><span style=\"font-size:16px;\">：</span><span style=\"font-size:16px;\">盈亏情况与指数对比（沪深300）、与其他客户对比、盈利top3/亏损top3（持有天数、盈亏额）等。</span><span style=\"line-height:1.5;font-size:16px;\">与投顾人员创建的模拟盘组合或策略组合进行对比（投顾收益）。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">5、交易分析</span><span style=\"font-size:16px;\">：</span><span style=\"font-size:16px;\">资金周转率、日交易成功率、平均仓位、持仓行业（行业仓位占比）、选股能力（选股板块、与板块收益率对比）、胜率、平均仓位、最大回撤等。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">6、</span><span style=\"font-size:16px;\">综合分析</span><span style=\"font-size:16px;\">：</span><span style=\"font-size:16px;\">选股能力、买点选择、绝对收益、看盘能力、抗跌能力等。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">7、持仓分析：包含总资金、总市值、资金余额、盈亏、昨日盈亏、累计收益率（近一年）、资产结构（现金、股票、基金、其他）、资金流向（交易、银行转账）等。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>服务优势</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">1、数据清洗及计算精准，为用户提供准备的账户分析；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">2、逻辑业务全，诊断维度全方位；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"> 3、在场景展现创意上与运营结合（比如对账单），为用户提供直观数据对比.。</span>\n</p>\n<p>\n<br/>\n</p>\n<p style=\"text-align:center;\">\n<span style=\"font-size:16px;\"><span style=\"line-height:24px;font-size:16px;\"><b>扫码体验</b></span></span>\n</p>\n<p style=\"text-align:center;\">\n<span style=\"font-size:small;\"><span style=\"line-height:24px;\"><b><img alt=\"\" src=\"https://www.hscloud.cn/api/201802011631000019.png\"/><br/>\n</b></span></span>\n</p>\n<p>\n</p><p>\n<span style=\"font-size:16px;\"><strong><br/>\n</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>商务咨询</strong></span>\n</p>\n<p>\n<span style=\"line-height:1.5;font-size:16px;\">咨询电话：0571-26693847</span>\n</p>\n<span style=\"font-size:16px;\"></span>\n</div>', 1, 'goods/images/70.jpg', 1, 0, '2018-12-22 10:59:43.363668', 4);
INSERT INTO `goods_goods` VALUES (71, '', 'iSee市场监控', 704, 8643, 6629, 999, 0.1, 0.1, '跟踪全市场情况，挖掘股市机会', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<span style=\"font-size:16px;\"><strong>服务介绍</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">实时跟踪全市场情况，进行个性化报告，根据用户画像帮助用户监控和定位股市机会。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>服务功能</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">1）大盘综述</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">为用户提供当日大盘表现、活跃度、领涨板块、领跌板块等实时数据；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">2</span><span style=\"font-size:16px;\">）</span><span style=\"font-size:16px;\">今日机会</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">根据每个用户的用户标签、个股标签、行为标签展示推荐股票名称、涨跌幅度与推荐理由；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">3）机会地图</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">根据每个用户的炒股习惯，通过不同的维度展现经常使用的选股工具。</span>\n</p>\n<p>\n<br/>\n</p>\n<span style=\"font-size:16px;\"><strong>服务优势</strong></span>\n<p>\n<span style=\"font-size:16px;\">可支持用户各类偏好设置，形成个性化的选股工具；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">支持和画像系统对接，更懂客户，更贴心。</span><span style=\"font-size:16px;\"></span>\n</p>\n<p>\n<br/>\n</p>\n<p style=\"text-align:center;\">\n<span style=\"font-size:16px;\"><span style=\"line-height:24px;font-size:16px;\"><b>扫码体验</b></span></span>\n</p>\n<p style=\"text-align:center;\">\n<span style=\"font-size:small;\"><span style=\"line-height:24px;\"><b><img alt=\"\" src=\"https://www.hscloud.cn/api/201802011630270018.png\"/><br/>\n</b></span></span>\n</p>\n<p style=\"text-align:center;\">\n<span style=\"font-size:small;\"><span style=\"line-height:24px;\"><b><br/>\n</b></span></span>\n</p>\n<p style=\"text-align:left;\">\n<span style=\"font-size:small;\"><span style=\"line-height:24px;\"><b><span style=\"font-size:16px;\"></span> </b></span></span>\n</p>\n<p>\n<b><span style=\"font-size:16px;\">商务咨询</span> </b>\n</p>\n<p>\n<b><span style=\"font-size:16px;\">咨询电话：0571-26693847</span> </b>\n</p>\n<b><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\"></span><br/>\n</b>\n<p>\n<br/>\n</p></div>', 1, 'goods/images/71.jpg', 0, 0, '2018-12-22 10:59:43.363668', 4);
INSERT INTO `goods_goods` VALUES (72, '', '晓鲸智能微信机器人', 543, 9665, 6776, 999, 0, 0, '一个专注于金融投资领域的智能对话机器人，为你解答关于金融投资的相关问题。', '<div class=\"hide service_info\" id=\"noapi_\"><img alt=\"\" src=\"https://www.hscloud.cn/api/201808151443350020.jpg\"/><img alt=\"\" src=\"https://www.hscloud.cn/api/201808151443430021.jpg\"/><img alt=\"\" src=\"https://www.hscloud.cn/api/201808151443510022.jpg\"/><img alt=\"\" src=\"https://www.hscloud.cn/api/201808151443580023.jpg\"/><img alt=\"\" src=\"https://www.hscloud.cn/api/201808151444040024.jpg\"/><img alt=\"\" src=\"https://www.hscloud.cn/api/201808151444110025.jpg\"/><br/></div>', 1, 'goods/images/72.jpg', 0, 0, '2018-12-22 10:59:43.363668', 4);
INSERT INTO `goods_goods` VALUES (73, '', '金融模拟平台', 769, 5092, 8431, 999, 0.1, 0.1, '支持各类交易类系统对接测试模拟', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<strong><span style=\"font-size:18px;\">服务介绍</span></strong>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:18px;\">•提供一站式全业务闭环测试验证解决方案，提高测试效率</span>\n</p>\n<p>\n<span style=\"font-size:18px;\">•支持上交所、深交所、期货交易所等各类业务</span>\n</p>\n<p>\n<span style=\"font-size:18px;\">•标准化接口，支持各类交易类系统对接</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<img alt=\"\" src=\"https://www.hscloud.cn/api/201803131021120005.jpg\"/>\n</p>\n<p>\n<span style=\"font-size:18px;\"><strong>服务特点</strong></span>\n</p>\n<p>\n<span style=\"font-size:18px;\"><strong><br/>\n</strong></span>\n</p>\n<p>\n<img alt=\"\" src=\"https://www.hscloud.cn/api/201803131017540004.png\"/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<strong><span style=\"font-size:18px;\">服务优势</span></strong>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:18px;\">1、业务品种齐全，提供云撮合的同时也提供全品类业务的清算功能</span>\n</p>\n<p>\n<span style=\"font-size:18px;\">2、撮合方式灵活丰富，提供客户操作控台，可以在四种不同的模式中自由切换  （见价成交，按行情成交，按比例成交，按对手盘成交）  </span>\n</p>\n<p>\n<span style=\"font-size:18px;\">3、我们提供7*24小时的连续测试服务，同时支持历史行情回放。</span>\n</p>\n<p>\n<span style=\"font-size:18px;\">4、提交申请流程简单，时间选择自由。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<br/>\n</p>\n<span style=\"font-size:18px;\">服务咨询：0571-28829063</span></div>', 1, 'goods/images/73.jpg', 0, 0, '2018-12-22 10:59:43.363668', 5);
INSERT INTO `goods_goods` VALUES (74, '', '模拟炒股大赛', 924, 8780, 9607, 999, 0.1, 0.1, '提供一站式模拟大赛支持', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<span style=\"font-size:16px;\"><strong> </strong></span>\n</p>\n<p>\n<strong style=\"line-height:1.5;\"><span style=\"font-size:16px;\">服务介绍：</span></strong>\n</p>\n<p>\n<span style=\"font-size:16px;\">模拟大赛采用实时行情价量进行撮合成交，为客户提供一站式模拟大赛服务，支持A股、港股正股、基金、股票期权、期货等全品种模拟投资大赛。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><img alt=\"\" src=\"https://www.hscloud.cn/api/201809251000110001.png\"/><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>服务特性：</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;background-color:#E53333;color:#FFFFFF;\">|大赛一站式服务</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">提供全套的大赛举办方案，覆盖前端、API（SDK）、后端服务等，协助金融机构快速举办仿真大赛，项目开展周期可控。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;background-color:#E53333;color:#FFFFFF;\">|多品种优势</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">模拟大赛支持多品种如A股、期货、股票期权、港股等模拟交易，交易撮合机制高度仿真。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">|两种对接模式统一接口服务</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">提供openAPI标准接口对接模式及Html5组件对接，期货、证券、股票期权等全业务支持。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong><br/>\n</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>大赛模式：</strong></span>\n</p>\n<p>\n<img align=\"\" alt=\"\" height=\"304\" src=\"https://www.hscloud.cn/api/201708260915400028.png\" title=\"\" width=\"850\"/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>功能介绍：</strong></span>\n</p>\n<p>\n<img alt=\"\" src=\"https://www.hscloud.cn/api/201708260916300029.png\" style=\"max-width:850px;\"/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>应用场景：</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">1）举办模拟大赛，扩大知名度，吸引新用户</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">2）举办内部赛事，进行人才选拔</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">3）建立模拟组合，提供观摩服务，发展投顾业务</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">4）大数据分析，拓展新业务</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>特别说明：</strong></span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n	①本服务套餐含2500个账户，超出2500账号部分，2元/个购买；\n</p>\n<p>\n	②本服务不包括行情服务费用，若需要提供行情接入服务，需另行签订服务订单。\n</p>\n<p>\n<br/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>咨询电话：0571-28829063</strong></span><span style=\"font-size:16px;\"></span>\n</p>\n<span style=\"font-size:16px;\"></span>\n<p>\n<br/>\n</p></div>', 1, 'goods/images/74.jpg', 0, 0, '2018-12-22 10:59:43.363668', 5);
INSERT INTO `goods_goods` VALUES (75, '', '模拟交易排名统计', 905, 5194, 6549, 999, 0.1, 0.1, '为模拟大赛提供真实排名统计', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<span style=\"font-size:16px;\"><strong>服务介绍：</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">在模拟交易所需的基础服务之外，模拟交易增值服务还支持查询账户资金排名、个人账户收益率排名、大赛收益率排名等特色服务。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>服务特性：</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\"></span><img alt=\"\" src=\"https://www.hscloud.cn/home/openapi/sharedirs_new/openportal/upload/image/ueditor/20170807/20170807145809000027.png\"/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>服务展示：</strong></span>\n</p>\n<p>\n<img alt=\"\" src=\"https://www.hscloud.cn/home/openapi/sharedirs_new/openportal/upload/image/ueditor/20170807/20170807143913000025.jpg\"/><span style=\"font-size:16px;\"> </span><img alt=\"\" src=\"https://www.hscloud.cn/home/openapi/sharedirs_new/openportal/upload/image/ueditor/20170807/20170807143951000026.jpg\"/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>咨询电话：</strong>0571-28829063</span>\n</p></div>', 1, 'goods/images/75.jpg', 0, 0, '2018-12-22 10:59:43.363668', 5);
INSERT INTO `goods_goods` VALUES (76, '', '期货模拟交易', 775, 8149, 6889, 999, 0.1, 0.1, '帮助投资者学习国内期货交易', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<strong style=\"font-size:16px;line-height:1.5;\">期货仿真交易</strong>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:1.5;\">       支持对接各交易所仿真环境，满足投资者开户的适当性要求。</span><span style=\"line-height:1.5;\">         </span>\n</p>\n<p>\n<span style=\"line-height:1.5;\"><img alt=\"\" src=\"https://www.hscloud.cn/api/201808211556020058.png\"/><br/>\n</span>\n</p>\n<strong style=\"font-size:16px;line-height:1.5;\">期货模拟交易</strong><br/>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><b>       </b>为客户提供高仿真期货交易实训环境，帮助金融从业人员，普通投资者熟悉国内期货交易机制，提高投资水平和风险管理能力。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>产品功能</strong></span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">1、模拟开户</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">支持客户通过仿真系统申请各类模拟账号，客户只需要输入手机号，即可分配模拟账户。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">2、模拟交易</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">云仿真模拟撮合机制完成按照交易所真实交易机制设置，以真实行情委托盘为排序依据，严格遵循价格及时间优先原则，进行价量成交。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">3、查询</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">支持委托查询、成交查询、持仓查询、资金查询以及历史委托查询、历史成交查询等。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">4、模拟后台</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">包括模拟账户系统和模拟柜台系统，实现模拟账户管理，模拟交易撮合、</span><span style=\"line-height:1.5;font-size:16px;\">模拟交易清算、模拟充值、模拟组合创建等功能。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">5、收益排行榜</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">支持盈利率排行，从中发现值得关注的炒股高手、达人。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">6、个性参数设置</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">费率设置：可设置各品种的交易手续费、保证金以及大赛交易品种的手续费、保证金等；；大赛规则设置：参数人数，起止时间，初始资金等。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<strong><span style=\"font-size:16px;\">产品亮点</span></strong>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">1、支持交易所所有期货品种</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">支持国内四家期货交易所的期货品种交易以及能源交易中心的原油期货模拟交易，交易时间与实盘一致。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">2、和真实行情一致</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">引入实盘的真实行情，模拟交易的成交价格和真实交易一致。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">3、T+0双向交易和对冲机制</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">投资者可以零成本学习期货业务，锻炼操盘技能，无论在牛市或熊市中均有获利机会</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">4、快速实现仿真大赛业务</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">5、竞赛数据管理方便</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">管理员有权限查阅每位参赛者的比赛业绩和相关数据，方便进行参赛者管理、并对比赛数据分析使用</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">6、组件化服务，组件化服务，可快速集成上线</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">7、界面可定制化</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>应用场景</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong><br/>\n</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">1、投资者教育</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">帮助客户打造以效果为核心，融合知识学习、学习评估、金融游戏、实盘模拟、能力评测、投资竞赛、行为分析为一体的投教方案，提高投教效果，为向投资者提供个性化服务提供数据。</span>\n</p>\n<p>\n<img alt=\"\" src=\"https://www.hscloud.cn/api/201804201114100005.jpg\"/>\n</p>\n<p>\n<span style=\"font-size:16px;\">2、营销活动</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">帮助客户快速举办各种投资比赛、大学生期货比赛、人机大战、量化策略大赛、营业部期货比赛等，提高活动效率、吸引更多客户参与</span>\n</p>\n<img alt=\"\" src=\"https://www.hscloud.cn/api/201804201120250006.jpg\"/>\n<p>\n<br/>\n</p>\n<span style=\"font-size:16px;\">3、金融游戏</span>\n<p>\n<span style=\"font-size:16px;\">•通过活泼有趣的游戏，吸引投资者打开APP，用轻松的形式传播投资理念、选股策略、风险控制等知识</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">•轻松管理游戏难度、关卡配置、积分奖励、社交分享</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">•自带积分管理功能，便于与其他应用互联</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<img alt=\"\" src=\"https://www.hscloud.cn/api/201804201227050010.jpg\"/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<strong><br/>\n</strong>\n</p>\n<p>\n<strong><span style=\"font-size:16px;\">特别说明：</span></strong>\n</p>\n<p>\n<span style=\"line-height:1.5;\">①本服务套餐含2500个账户，超出2500账号部分，2元/个购买；</span>\n</p>\n<p>\n	②本服务不包括行情服务费用，若需要提供行情接入服务，需另行签订服务订单。\n</p>\n<p>\n<br/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>咨询电话：</strong>0571-28829063</span>\n</p></div>', 1, 'goods/images/76.jpg', 0, 0, '2018-12-22 10:59:43.363668', 5);
INSERT INTO `goods_goods` VALUES (77, '', '港股模拟交易', 493, 5685, 7900, 999, 0.1, 0.1, '帮助投资者学习港股交易', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<span style=\"font-size:16px;\"><strong>服务介绍</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong><br/>\n</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">模拟港股交易，为客户提供高仿真港股交易实训环境，帮助金融从业人员，普通投资者熟悉港股交易机制，提高投资水平和风险管理能力。</span><span style=\"font-size:16px;\"></span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>产品功能</strong></span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">1、模拟开户</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">支持客户通过仿真系统申请港股模拟账号，客户只需要输入手机号，即可分配港股模拟账户。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">2、模拟交易</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">云仿真模拟撮合机制完成按照交易所真实交易机制设置，以真实行情委托盘为排序依据，严格遵循价格及时间优先原则，进行价量成交。支持分红、送股、增发因素。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">3、</span><span style=\"line-height:1.5;font-size:16px;\">收益排行榜</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">支持收益率排行，从中发现值得关注的炒股高手、达人。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">4、模拟后台</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">包括模拟账户系统和模拟柜台系统，实现模拟账户管理，模拟交易撮合、</span><span style=\"line-height:1.5;font-size:16px;\">、模拟交易清算、模拟充值、查询等功能。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<strong><span style=\"font-size:16px;\">产品亮点</span></strong>\n</p>\n<p>\n<strong><span style=\"font-size:16px;\"><br/>\n</span></strong>\n</p>\n<p>\n<span style=\"font-size:16px;\">T+0交易随时卖</span><span style=\"font-size:16px;\">、可买涨可卖空、交易流程高度拟合、</span><span style=\"font-size:16px;\">快速实现港股仿真大赛业务、</span><span style=\"font-size:16px;\">组件化服务，快速对接，一周可上线</span><span style=\"font-size:16px;\"></span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>应用场景</strong></span>