INSERT INTO `goods_goods` VALUES (86, '', '身份证OCR识别', 346, 9941, 9542, 999, 0.1, 0.1, '自动识别身份证上的信息', '<div class=\"hide service_info\" id=\"noapi_\"><p>\n<span style=\"font-size:16px;\"><strong>服务简介：</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">通过手机拍摄身份证图像或者从手机相册中加载证件图像，过滤身份证的背景底纹干扰，自动分析证件各文字进行字符切分、识别，最后将识别结果按姓名、地址、民族、身份证号等项目分别导入到软件的数据库对应的字段当中。</span>\n</p>\n<p align=\"left\">\n	       <img alt=\"\" src=\"https://www.hscloud.cn/api/20170421103445000334.png\"/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>服务功能：</strong></span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\">（1）识别精度高、速度快；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">（2）api接口形式，各种终端设备、各种开发环境全支持；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">（3）支持身份证头像提取，广泛应用实人认证，达到人证合一；</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">（4）支持多线程支持、拍照识别、扫描识别。</span><span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\"><strong>注意事项：</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">OCR识别服务拍摄注意事项</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">见论坛：https://bbs.hscloud.cn/read-1548.html</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>合作场景：</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">身份证OCR识别广泛适用于保险、证券非现场开户、移动警务、社区管理、流动人口管理、信用卡申请等各领域。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">随着移动互联网的发展，可以预见未来几年60%以上的业务将会逐渐转移到智能终端系统上来。现在在软件和app接入身份证ocr识别接口，就可在进行实名认证时，省去了人工录入，减少了误输入，提高了效率，增强了用户的体验感。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\">比如录入证件信息：互联网金融和移动支付实名制，证券在线开户，网上购买保险。公安执法人员移动执法，银行工作人员办理信用卡，网络实名制，二手车交易等场景，都会涉及证件信息的录入，OCR证件识别自动录入证件信息，再也不用耗时耗力了。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>对接流程：</strong></span>\n</p>\n<p>\n<span style=\"font-size:16px;\">注册/登录网站——购买服务——查看API文档——接口对接开发</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><strong>商务咨询：</strong>0571-26691344</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<span style=\"font-size:10px;\">（该服务由第三方服务机构提供）</span>\n</p>\n<p>\n<span style=\"font-size:10px;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:10px;\"><a class=\"ke-insertfile\" href=\"https://www.hscloud.cn/home/openapi/sharedirs_new/openportal/upload/image/ueditor/20170906/20170906100022000042.xlsx\" target=\"_blank\"><span style=\"font-size:16px;\">身份证OCR识别接口文档</span></a><br/>\n</span>\n</p></div>', 1, 'goods/images/86.jpg', 0, 0, '2018-12-22 10:59:43.363668', 6);
