INSERT INTO `goods_goods` VALUES (93, '', '晓鲸决战吧', 390, 6891, 5731, 999, 0.1, 0.1, '在线答题，决胜千里', '<div class=\"hide service_info\" id=\"noapi_\"><h1>\n<strong><span style=\"font-size:16px;background-color:#FFE500;\"><span style=\"font-size:14px;line-height:2;\"></span><span style=\"font-size:16px;line-height:2;\">产品服务介绍</span></span></strong>\n</h1>\n<p>\n<span style=\"font-size:16px;\"></span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:2;\">通过答题的形式，进行线上的投资者教育内容的传播、品牌曝光，可在活动形式中关联本身的用户体系积分和奖品体系，进行用户的线上活动运营转化。</span>\n</p>\n<p>\n<span style=\"font-size:16px;line-height:2;\">晓鲸决战吧（二期）在单人闯关赛的基础上新推出PK赛，提供更丰富的游戏模式，增添游戏趣味性，强化用户粘性。</span>\n</p>\n<p>\n<strong><span style=\"font-size:16px;line-height:2;\">单人闯关赛</span></strong><span style=\"font-size:16px;line-height:2;\">：共8关，在规定游戏时间内，答对所有题目，挑战成功，获得新成就，解锁下一关，并得到鲸喜值奖励。</span>\n</p>\n<p>\n<strong><span style=\"font-size:16px;line-height:2;\">PK赛</span></strong><span style=\"font-size:14px;line-height:2;\"><span style=\"font-size:16px;\">：共5个段位，玩家使用鲸喜值进入PK赛，系统自动匹配对战玩家。单局游戏共5道题，每题答题时间15秒，答题越慢分越少，最后一道</span><span style=\"font-size:16px;\">题双倍加分，得分高者胜。获胜一局点亮半颗段位星，获得鲸喜值奖励。段位星满即可解锁下一段位。参与PK赛玩家可在PK赛排行榜查看百强游戏玩家。</span></span>\n</p>\n<p>\n<strong><span style=\"font-size:16px;line-height:2;\">晓鲸透题</span></strong><span style=\"font-size:16px;line-height:2;\">：游戏题目难度太大，晓鲸机器人送“福卡”。点击晓鲸透题，进入晓鲸百问，开启狂刷模式，助你通关拿鲸喜。</span>\n</p>\n<p>\n<br/>\n</p>\n<p>\n<br/>\n</p>\n<h1>\n<strong><span style=\"font-size:16px;line-height:2;background-color:#FFE500;\">服务功能</span></strong>\n</h1>\n<h2>\n<strong><span style=\"font-size:16px;line-height:2;\">1）  页面内容设置</span></strong>\n</h2>\n<p>\n<span style=\"font-size:16px;line-height:2;\">进行基础的答题活动线上页面模板提供，可以设置基础的答题活动名称、规则等页面内容的设置，可进行关卡名称、奖品、兑换规则配置，个性化配置PK赛入场费、单局游戏奖励。</span>\n</p>\n<h2>\n<strong><span style=\"font-size:16px;line-height:2;\">2）  题库设置</span></strong>\n</h2>\n<p>\n<span style=\"font-size:16px;line-height:2;\">可进行个性化的答题活动题库设置，上传自定义题库内容，实现题库内容的个性化设置，自定义题数量。</span>\n</p>\n<h2>\n<strong><span style=\"font-size:16px;line-height:2;\">3）  上线部署</span></strong>\n</h2>\n<p>\n<span style=\"font-size:16px;line-height:2;\">跟进活动页面上线部署过程，还可支持个性化数据需求。</span>\n</p>\n<p>\n<br/>\n</p>\n<h1>\n<strong><span style=\"font-size:16px;line-height:2;background-color:#FFE500;\">服务优势</span></strong>\n</h1>\n<p>\n<span style=\"font-size:16px;line-height:2;\">具有多项优秀先行案例，可根据具体的活动内容需求进行页面前端、实际部署、数据需求的个性化改造。</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><span style=\"font-size:16px;line-height:2;background-color:#FFE500;\"><strong>活动展示</strong></span><span style=\"background-color:#FFE500;\"><strong></strong></span><br/>\n</span>\n</p>\n<p>\n<span style=\"font-size:16px;\"><span style=\"background-color:#FFE500;\"><strong><img alt=\"\" src=\"https://www.hscloud.cn/api/201807241559460027.png\"/><br/>\n</strong></span></span>\n</p>\n<p>\n<span style=\"font-size:16px;\"> </span>\n</p>\n<p>\n<img alt=\"\" src=\"https://www.hscloud.cn/api/201807241559550028.png\"/>\n</p>\n<h1>\n<span style=\"font-size:16px;background-color:#FFE500;\"><strong><img alt=\"\" src=\"https://www.hscloud.cn/api/201807241600030029.png\"/><br/>\n</strong></span>\n</h1>\n<p>\n<br/>\n</p>\n<p>\n<br/>\n</p></div>', 1, 'goods/images/93.jpg', 0, 0, '2018-12-22 10:59:43.363668', 7);
